  // onClearCompleted = () => {
  //   this.setState(({ taskData }) => {
  //     const newArr = taskData.filter((task) => {
  //       return task.active;
  //     });
  //     return {
  //       taskData: newArr,
  //     };
  //   });
  // };

  // onEdit = (id) => {
  //   this.setState(({ taskData }) => {
  //     const idx = taskData.findIndex((e) => e.id === id);
  //     const oldItem = taskData[idx];
  //     const newItem = { ...oldItem, edit: !oldItem.edit };
  //     const newArr = [...taskData.slice(0, idx), newItem, ...taskData.slice(idx + 1)];
  //     return {
  //       taskData: newArr,
  //     };
  //   });
  // };

  // handleEditTask = (id, text) => {
  //   this.setState(({ taskData }) => {
  //     const idx = taskData.findIndex((e) => e.id === id);
  //     const oldItem = taskData[idx];
  //     const newItem = { ...oldItem, label: text, edit: false };
  //     const newArr = [...taskData.slice(0, idx), newItem, ...taskData.slice(idx + 1)];
  //     return {
  //       taskData: newArr,
  //     };
  //   });
  // };

  // onFilterChange = (filter) => {
  //   this.setState({ filter });
  // };

  state = {
    taskData: [
      this.createTaskItem('Сompleted task'),
      this.createTaskItem('Editing task'),
      this.createTaskItem('Active task'),
    ],
    filter: 'all',
  };

  createTaskItem(label) {
    return {
      label: label,
      id: Math.floor(Math.random() * 1000000),
      completed: false,
      active: true,
      edit: false,
    };
  }

  onFilterChange = (filter) => {
    this.setState({
      filter,
    });
  };

  onEdit = (id) => {
    this.toggleProperty(id, 'editing');
  };

  onToggleCompleted = (id) => {
    this.toggleProperty(id, 'completed');
  };

  addTask = (text) => {
    const newItem = this.createTaskItem(text);

    this.setState(({ taskData }) => {
      const newArr = [...taskData, newItem];
      return {
        taskData: newArr,
      };
    });
  };

  onEdit = (id, text) => {
    this.setState(({ statusItem }) => {
      const newArr = statusItem.map((el, i) => {
        if (el.id === id) {
          el.text = text;
          el.edit = !statusItem[i].edit;
        }
        return el;
      });
      return {
        statusItem: newArr,
      };
    });
  };

  deleteTask = (id) => {
    this.setState(({ statusItem }) => ({
      statusItem: statusItem.filter((item) => item.id !== id),
    }));
  };

  toggleProperty = (id, propName) => {
    this.setState(({ statusItem }) => {
      const newArr = statusItem.map((el, i) => {
        if (el.id === id) {
          el[propName] = !statusItem[i][propName];
        }
        return el;
      });
      return {
        statusItem: newArr,
      };
    });
  };

  filter(items, filter) {
    switch (filter) {
      case 'all':
        return items;
      case 'active':
        return items.filter((item) => item.active);
      case 'done':
        return items.filter((item) => !item.active);
      default:
        return items;
    }
  }

  onclearCompleted = () => {
    this.setState(({ statusItem }) => ({
      statusItem: statusItem.filter((el) => !el.completed),
    }));
  };

  render() {
    const taskCount = this.state.taskData.filter((e) => e.active).length;
    const visibleTasks = this.filter(this.state.taskData, this.state.filter);
    return (
      <section className="todoapp">
        <NewTaskForm onAddTask={this.addTask} />
        <section className="main">
          <TaskList
            taskData={visibleTasks}
            onDeleted={this.deleteTask}
            onToggleDone={this.onToggleDone}
            onEdit={this.onEdit}
            handleEditTask={this.handleEditTask}
          />
          <Footer
            count={taskCount}
            filter={this.state.filter}
            onFilterChange={this.onFilterChange}
            onClearCompleted={this.onClearCompleted}
          />
        </section>
      </section>
    );
  }
}
